import { useState } from 'react';
import './App.css';
import Home from './Components/Home';
import Quiz from './Components/Quiz';

function App() {

  const [gameState, setGameState] = useState("home");

  return (
    <div className="App">
      <h1>Quiz App</h1>

      {gameState === "home" && <Home />}
      {gameState === "quiz" && <Quiz />}
      {gameState === "end" && <End />}

      </div>
  );
}

export default App;
